tabs_recordsheet.script:
function onInit()
	if super and super.onInit then
		super.onInit();
	end
	local nodeRecord = window.getDatabaseNode();
	local isIdentifiedPath = nodeRecord.getPath("isidentified");
	DB.addHandler(isIdentifiedPath, "onUpdate", onIdentifiedChanged);
	update(DB.getValue(isIdentifiedPath) ~= 0);
end
function onClose()
	local nodeRecord = window.getDatabaseNode();
	DB.removeHandler(nodeRecord.getPath("isidentified"), "onUpdate", onIdentifiedChanged);
	if super and super.onClose then
		super.onClose();
	end
end
function onIdentifiedChanged(nodeIdentified)
	local bIdentified = nodeIdentified.getValue() ~= 0;
	update(bIdentified);
end
function update(bIdentified)
	if bIdentified or Session.IsHost then
		setTab(2, "actions", "tab_actions");
	else
		setTab(2, "empty_actions", "tab_actions");
	end
end

stringcontrol.script:
function onInit()
	local node = window.getDatabaseNode().getChild("count");
	if node then
		setVisible(true);
		update(node);
		DB.addHandler(node.getPath(), "onUpdate", update);
	end
end
function onClose()
	local node = window.getDatabaseNode().getChild("count");
	if node then
		DB.removeHandler(node.getPath(), "onUpdate", update);
	end
end
function update(node)
	setValue(string.format("(x%i)", node.getValue()));
end

label_charframetop.script:
function onClickDown()
	return true;
end

function onClickRelease()
	local bVisible = window.charges.isVisible();
	if bVisible then
		setFont("subwindowsmalltitle_disabled");
		window.charges.setVisible(false);
	else
		setFont("subwindowsmalltitle");
		window.charges.setVisible(true);
	end
	return true;
end

function onClickDown()
	return true;
end

function onClickRelease()
	local bVisible = window.powerlist.isVisible();
	if bVisible then
		setFont("subwindowsmalltitle_disabled");
		window.powerlist.setVisible(false);
	else
		setFont("subwindowsmalltitle");
		window.powerlist.setVisible(true);
	end
	return true;
end

windowlist.script:
function update()
	local bEditMode = (window.powerlist_iedit.getValue() == 1);
	for _,win in pairs(getWindows()) do
		win.idelete.setVisibility(bEditMode);
	end
end
function addEntry(bFocus)
	local win = createWindow();
	if bFocus and win then
		win.name.setFocus();
	end
	return win;
end
function onListChanged()
	if super and super.onListChanged then
		super.onListChanged();
	end
	window.chargetracker.onChargesChanged();
end

button_stringcycler.script:
local initialize2Original;
function onInit()
	if super then
		initialize2Original = super.initialize2;
		super.initialize2 = initialize2;
		if super.onInit then
			super.onInit();
		end
	end
end
function initialize2(sLabels, sValues, sDefaultLabel, sInitialValue)
	if ExtendedRest then
		sLabels = sLabels .. "|extended_rest_label";
		sValues = sValues .. "|extended";
	end
	initialize2Original(sLabels, sValues, sDefaultLabel, sInitialValue);
end
